import './home.css';
import fact2 from '../images/Fact-2.png'
import fact1 from '../images/Fact-1.png'
import React, { useRef, useState } from 'react';
// Import Swiper React components
import { Swiper, SwiperSlide } from 'swiper/react';

// Import Swiper styles
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/pagination';

import './style.css';

// import required modules
import { EffectCoverflow, Pagination } from 'swiper/modules';



function App() {
  
  return (
    <><div className="hero min-h-screen" style={{ backgroundImage: 'url(https://images.unsplash.com/photo-1542601906990-b4d3fb778b09?q=80&w=1913&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D)' }}>
      <div className="hero-overlay bg-opacity-10"></div>
      <div className="hero-content text-center text-neutral-content">
        <div className="max-w-md">
          <h1 className="mb-5 text-5xl font-bold">EcoVibes</h1>
          <p className="mb-5 text-1xl font-bold">"EcoVibes" is your hub for embracing sustainable living in style. Delve into our sections dedicated to food, gardening, and recycling, where you'll discover a trove of eco-friendly tips, trends, and solutions. From mouthwatering plant-based recipes to lush garden guides and innovative recycling hacks, EcoVibes invites you to elevate your lifestyle while making a positive impact on the planet. Join our vibrant community of eco-enthusiasts and start living in harmony with nature today.</p>
          <button className="btn btn-primary" href='section.js'>Get Started</button>
        </div>
      </div>
    </div><>
        <Swiper
          effect={'coverflow'}
          grabCursor={true}
          centeredSlides={true}
          slidesPerView={'auto'}
          coverflowEffect={{
            rotate: 50,
            stretch: 0,
            depth: 100,
            modifier: 1,
            slideShadows: true,
          }}
          pagination={true}
          modules={[EffectCoverflow, Pagination]}
          className="mySwiper"
        >
          <SwiperSlide>
            <img src="https://images.unsplash.com/photo-1712315481738-a77f7f78f2cb?q=80&w=1887&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" />
          </SwiperSlide>
          <SwiperSlide>
            <img src={fact2} />
          </SwiperSlide>
          <SwiperSlide>
            <img src={fact1} />
          </SwiperSlide>
          <SwiperSlide>
            <img src="../images/Fact-3.png" />
          </SwiperSlide>
          <SwiperSlide>
            <img src="../images/Fact-4.jpg" />
          </SwiperSlide>
          <SwiperSlide>
            <img src="https://swiperjs.com/demos/images/nature-6.jpg" />
          </SwiperSlide>
          <SwiperSlide>
            <img src="https://swiperjs.com/demos/images/nature-7.jpg" />
          </SwiperSlide>
          <SwiperSlide>
            <img src="https://swiperjs.com/demos/images/nature-8.jpg" />
          </SwiperSlide>
          <SwiperSlide>
            <img src="https://swiperjs.com/demos/images/nature-9.jpg" />
          </SwiperSlide>
        </Swiper>
      </></>

      
  );
}

export default App;
