import React, { useState } from 'react';
import axios from 'axios';

const ItemForm = () => {
    const [item, setItem] = useState({
        name: '',
        description: '',
        location: '',
        imageUrl: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setItem({ ...item, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:8080/api/items', item)
            .then(response => {
                alert('Item created successfully');
                // Handle item creation success (e.g., redirect to item list)
            })
            .catch(error => {
                console.error('There was an error creating the item!', error);
            });
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" name="name" placeholder="Item Name" onChange={handleChange} />
            <textarea name="description" placeholder="Description" onChange={handleChange}></textarea>
            <input type="text" name="location" placeholder="Location" onChange={handleChange} />
            <input type="text" name="imageUrl" placeholder="Image URL" onChange={handleChange} />
            <button type="submit">Create Item</button>
        </form>
    );
};

export default ItemForm;
