import React, { useState, useEffect } from 'react';
import PostService from './PostService';
import Nav from "./components/Nav"

const PostList = () => {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        loadPosts();
    }, []);

    const loadPosts = async () => {
        const result = await PostService.getPosts();
        setPosts(result.data);
    };

    const deletePost = async (id) => {
        await PostService.deletePost(id);
        loadPosts();
    };

    return (
        <><Nav></Nav><div className="min-h-screen bg-gray-100">
            <div className="container mx-auto px-1 pt-8 pb-16 max-w-2g">
                <h2 className="text-center text-2xl font-bold text-gray-800 mb-8">Recycling Ideas</h2>
                {posts.map(post => (
                    <div key={post.id} className="bg-white shadow-md rounded-lg overflow-hidden mb-8">
                        <div className="p-4">
                            <div className="flex items-center mb-2">
                                <div className="h-10 w-10 rounded-full bg-gray-500"></div>
                                <div className="ml-3">
                                    <div className="text-sm font-medium text-gray-900">{post.name}</div>
                                    <div className="text-sm text-gray-500">{post.email}</div>
                                </div>
                                <div className="ml-auto text-sm text-gray-500">
                                    {new Date(post.date).toLocaleDateString()}
                                </div>
                            </div>
                            <h3 className="text-lg font-semibold text-gray-900 mb-2">{post.title}</h3>
                            <p className="text-gray-700 mb-4">{post.content}</p>
                            <button
                                onClick={() => deletePost(post.id)}
                                className="bg-red-500 text-white px-4 py-2 rounded-full hover:bg-red-600"
                            >
                                Delete
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div></>
    );
};

export default PostList;
