import React, { useEffect, useState } from "react";
import materialData from "./data.json";
import './styless.css'
import { Link } from "react-router-dom";
import Nav from '../Nav'

function App() {
  const [searchResults, setSearchResults] = useState([]);
  const [query, setQuery] = useState("");
  const [searchParameters] = useState(["material", "recyclingInfo"]);

  useEffect(() => {
    setSearchResults(materialData);
  }, []);

  function search(data) {
    return data.filter((item) =>
      searchParameters.some((parameter) =>
        item[parameter].toString().toLowerCase().includes(query.toLowerCase())
      )
    );
  }

  return (
    <><Nav></Nav><div className="containers">
              <center>
                  <h1>Search for the item</h1>
              </center>
              <div className="input-box">
                  <input
                      type="search"
                      name="search-form"
                      id="search-form"
                      className="search-input"
                      onChange={(e) => setQuery(e.target.value)}
                      placeholder="Search material or recycling info" />
              </div>
              <center>
                  {search(searchResults).map((item) => {
                      return (
                          <div className="box" key={item.id}>
                              <div className="cards">
                                  <div className="category">{item.material}</div>
                                  <div className="heading">
                                      {item.recyclingInfo}
                                  </div>
                              </div>
                          </div>
                      );
                  })}
              </center>
          </div></>
  );
}

export default App;
