import React, { useState } from 'react';
import PostService from './PostService';

const CreatePost = (props) => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');

    const savePost = async (e) => {
        e.preventDefault();
        let post = { title, content };
        await PostService.createPost(post);
        props.history.push('/posts');
    };

    return (
        <div>
            <h2 className="text-center">Add Post</h2>
            <form>
                <div className="form-group">
                    <label>Title:</label>
                    <input placeholder="Title" name="title" className="form-control" value={title} onChange={e => setTitle(e.target.value)} />
                </div>
                <div className="form-group">
                    <label>Content:</label>
                    <textarea placeholder="Content" name="content" className="form-control" value={content} onChange={e => setContent(e.target.value)} />
                </div>
                <button className="btn btn-success" onClick={savePost}>Save</button>
            </form>
        </div>
    );
};

export default CreatePost;
