// src/components/ItemCard.js
import React from 'react';

const ItemCard = ({ item }) => {
  return (
    <div className="bg-white shadow-md rounded-lg overflow-hidden">
      {item.photoUrl && (
        <img
          src={item.photoUrl}
          alt={item.title}
          className="w-full h-64 object-cover"
        />
      )}
      <div className="p-4">
        <h2 className="text-xl font-bold mb-2">{item.title}</h2>
        <p className="text-gray-700 mb-2">{item.description}</p>
        <p className="text-gray-900 font-semibold mb-2">Category: {item.category}</p>
        <p className="text-gray-900 font-semibold mb-2">Condition: {item.condition}</p>
        <p className="text-gray-900 font-semibold mb-2">Location: {item.location}</p>
        <p className="text-gray-900 font-semibold mb-2">Contact: {item.contactNumber}</p>
        <p className="text-gray-900 font-bold text-lg">â‚¹{item.price.toFixed(2)}</p>
      </div>
    </div>
  );
}

export default ItemCard;
