import "./style.css";
import React, { useEffect, useState } from "react";
import data from "./data.json";

function App() {
  const [data, setData] = useState([]);
  const [query, setQuery] = useState("");
  const [searchParameters, setSearchParameters] = useState([]);

  useEffect(() => {
    setData(data);
    setSearchParameters(["material", "recyclingInfo"]);
  }, []);

  function search(data) {
    return data.filter((item) =>
      searchParameters.some((parameter) =>
        item[parameter].toString().toLowerCase().includes(query.toLowerCase())
      )
    );
  }

  return (
    <div className="container">
      <center>
        <h1>Search component in ReactJS</h1>
      </center>
      <div className="input-box">
        <input
          type="search"
          name="search-form"
          id="search-form"
          className="search-input"
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search material or recycling info"
        />
      </div>
      <center>
        {search(data).map((item) => {
          return (
            <div className="box">
              <div className="card">
                <div className="category">{item.material}</div>
                <div className="heading">
                  {item.recyclingInfo}
                </div>
              </div>
            </div>
          );
        })}
      </center>
    </div>
  );
}

export default App;
