import React, { useState } from 'react';
import axios from 'axios';

const AddItem = () => {
    const [item, setItem] = useState({
        title: '',
        description: '',
        category: '',
        price: '',
        condition: '',
        location: '',
        contactNumber: ''
    });
    const [photo, setPhoto] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setItem(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handlePhotoChange = (e) => {
        setPhoto(e.target.files[0]);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('item', new Blob([JSON.stringify(item)], { type: 'application/json' }));
        if (photo) {
            formData.append('photo', photo);
        }

        axios.post('http://localhost:8002/api/items', formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        })
            .then(response => {
                console.log('Item added:', response.data);
                // Optionally clear the form
                setItem({
                    title: '',
                    description: '',
                    category: '',
                    price: '',
                    condition: '',
                    location: '',
                    contactNumber: ''
                });
                setPhoto(null);
            })
            .catch(error => {
                console.error('Error adding item:', error);
            });
    };

    return (
        <div className="max-w-xl mx-auto p-6 bg-white shadow-md rounded-lg">
            <h2 className="text-2xl font-bold mb-6 text-center">Add Item</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block text-gray-700">Title:</label>
                    <input
                        type="text"
                        name="title"
                        value={item.title}
                        onChange={handleChange}
                        className="w-full p-2 border border-white rounded mt-1"
                    />
                </div>
                <div>
                    <label className="block text-gray-700">Description:</label>
                    <textarea
                        name="description"
                        value={item.description}
                        onChange={handleChange}
                        className="w-full p-2 border border-gray-300 rounded mt-1"
                    />
                </div>
                <div>
                    <label className="block text-gray-700">Category:</label>
                    <input
                        type="text"
                        name="category"
                        value={item.category}
                        onChange={handleChange}
                        className="w-full p-2 border border-white rounded mt-1"
                    />
                </div>
                <div>
                    <label className="block text-gray-700">Price:</label>
                    <input
                        type="number"
                        name="price"
                        value={item.price}
                        onChange={handleChange}
                        className="w-full p-2 border border-gray-300 rounded mt-1"
                    />
                </div>
                <div>
                    <label className="block text-gray-700">Condition:</label>
                    <input
                        type="text"
                        name="condition"
                        value={item.condition}
                        onChange={handleChange}
                        className="w-full p-2 border border-gray-300 rounded mt-1"
                    />
                </div>
                <div>
                    <label className="block text-gray-700">Location:</label>
                    <input
                        type="text"
                        name="location"
                        value={item.location}
                        onChange={handleChange}
                        className="w-full p-2 border border-gray-300 rounded mt-1"
                    />
                </div>
                <div>
                    <label className="block text-gray-700">Contact Number:</label>
                    <input
                        type="text"
                        name="contactNumber"
                        value={item.contactNumber}
                        onChange={handleChange}
                        className="w-full p-2 border border-gray-300 rounded mt-1"
                    />
                </div>
                <div>
                    <label className="block text-gray-700">Photo:</label>
                    <input
                        type="file"
                        onChange={handlePhotoChange}
                        className="w-full p-2 border border-gray-300 rounded mt-1"
                    />
                </div>
                <button type="submit" className="w-full py-2 px-4 bg-blue-600 text-white font-semibold rounded mt-4 hover:bg-blue-700">
                    Add Item
                </button>
            </form>
        </div>
    );
};

export default AddItem;
