import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar, Pie } from 'react-chartjs-2';

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = () => {
    setLoading(true);
    axios.get('http://localhost:8005/api/dashboard')
      .then(response => {
        setDashboardData(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError('Error fetching dashboard data. Please try again later.');
        setLoading(false);
      });
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  if (!dashboardData) {
    return null; // Optionally, render a loading spinner or placeholder
  }

  const { totalWasteOrdered, wasteCategoryData } = dashboardData;

  // Prepare data for charts
  const categoryLabels = Object.keys(wasteCategoryData);
  const categoryQuantities = Object.values(wasteCategoryData);

  // Chart data and options for Total Waste Ordered (Bar Chart)
  const barChartData = {
    labels: ['Total Waste Ordered'],
    datasets: [
      {
        label: 'Total Waste Ordered (kg)',
        backgroundColor: 'rgba(54, 162, 235, 0.6)',
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 1,
        hoverBackgroundColor: 'rgba(54, 162, 235, 0.8)',
        hoverBorderColor: 'rgba(54, 162, 235, 1)',
        data: [totalWasteOrdered],
      },
    ],
  };

  const barChartOptions = {
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          callback: function(value) { return value + ' kg'; }
        }
      }
    }
  };

  // Chart data and options for Waste Category Data (Pie Chart)
  const pieChartData = {
    labels: categoryLabels,
    datasets: [
      {
        label: 'Waste Category Data',
        backgroundColor: [
          '#FF6384',
          '#36A2EB',
          '#FFCE56',
          '#66FF99',
          '#FF99FF',
          '#FF9966',
        ],
        hoverBackgroundColor: [
          '#FF6384',
          '#36A2EB',
          '#FFCE56',
          '#66FF99',
          '#FF99FF',
          '#FF9966',
        ],
        data: categoryQuantities,
      },
    ],
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Dashboard</h1>

      <div className="bg-white shadow-md rounded-lg p-6 mb-6">
        <h2 className="text-xl font-bold mb-4">Total Waste Ordered: {totalWasteOrdered} kg</h2>
        <Bar data={barChartData} options={barChartOptions} />

        <h2 className="text-xl font-bold mb-4 mt-8">Waste Category Data:</h2>
        <Pie data={pieChartData} />
      </div>
    </div>
  );
};

export default Dashboard;
