import React from 'react';
import { Link } from 'react-router-dom';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/pagination';
import { EffectCoverflow, Pagination } from 'swiper/modules';

import Nav from '../Nav';
import Text from '../text';
import Section from '../Section';
import fact1 from '../images/Fact-1.png';
import fact2 from '../images/Fact-2.png';
import fact3 from '../images/Fact-3.png';
import fact4 from '../images/Fact-4.jpg';
import fact5 from '../images/Fact-5.jpg';
import fact6 from '../images/Fact-6.jpg';
import fact7 from '../images/Fact-7.jpg';
import fact8 from '../images/Fact-8.jpg';
import fact9 from '../images/Fact-9.jpg';

function Home() {
  return (
    <>
      <Nav />
      <div className="flex flex-col md:flex-row items-center justify-center md:space-x-8 bg-cover bg-center min-h-screen" style={{ backgroundImage: 'url(https://images.unsplash.com/photo-1542601906990-b4d3fb778b09?q=80&w=1913&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D)' }}>
        <div className="p-8 max-w-md text-center md:text-left">
          <h1 className="mb-5 text-5xl font-bold text-gray-300">Sustainify</h1>
          <p className="mb-5 text-lg text-gray-200">"Sustainify" is your hub for embracing sustainable living in style. Delve into our sections dedicated to food, gardening, and recycling, where you'll discover a trove of eco-friendly tips, trends, and solutions. From mouthwatering plant-based recipes to lush garden guides and innovative recycling hacks, EcoVibes invites you to elevate your lifestyle while making a positive impact on the planet. Join our vibrant community of eco-enthusiasts and start living in harmony with nature today.</p>
          <Link to="/login" className="btn btn-primary">Get Started</Link>
        </div>
        <div className="md:w-1/2">
          <Swiper
            effect={'coverflow'}
            grabCursor={true}
            centeredSlides={true}
            slidesPerView={'auto'}
            coverflowEffect={{
              rotate: 50,
              stretch: 0,
              depth: 100,
              modifier: 1,
              slideShadows: true,
            }}
            pagination={true}
            modules={[EffectCoverflow, Pagination]}
            className="mySwiper"
          >
            <SwiperSlide><img src={fact9} alt="Fact 9" /></SwiperSlide>
            <SwiperSlide><img src={fact1} alt="Fact 1" /></SwiperSlide>
            <SwiperSlide><img src={fact8} alt="Fact 8" /></SwiperSlide>
            <SwiperSlide><img src={fact3} alt="Fact 3" /></SwiperSlide>
            <SwiperSlide><img src={fact5} alt="Fact 5" /></SwiperSlide>
            <SwiperSlide><img src={fact4} alt="Fact 4" /></SwiperSlide>
            <SwiperSlide><img src={fact6} alt="Fact 6" /></SwiperSlide>
            <SwiperSlide><img src={fact2} alt="Fact 2" /></SwiperSlide>
            <SwiperSlide><img src={fact7} alt="Fact 7" /></SwiperSlide>
          </Swiper>
        </div>
      </div>
      <Text />
      <Section />
    </>
  );
}

export default Home;
