import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Nav from "./Nav";
import { useHistory } from 'react-router-dom';

const ItemCard = ({ item }) => {
  const handleBuyNow = () => {
    //const phoneNumber = '8277492748'; // Replace with your phone number
    const whatsappUrl = `https://wa.me/${item.contactNumber}`;
    window.open(whatsappUrl, '_blank');
  };

  return (
    <div className="bg-white shadow-lg rounded-lg overflow-hidden transform transition duration-500 hover:scale-105">
      {item.photoUrl && (
        <img
          src={`http://localhost:8005/api/items/photos/${item.photoUrl}`}
          alt={item.title}
          className="w-full h-64 object-cover"
        />
      )}
      <div className="p-4">
        <h2 className="text-xl font-bold mb-2">{item.title}</h2>
        <p className="text-gray-700 mb-2">{item.description}</p>
        <p className="text-gray-900 font-semibold mb-2">Category: {item.category}</p>
        <p className="text-gray-900 font-semibold mb-2">Quantity: {item.quantity}</p>
        <p className="text-gray-900 font-semibold mb-2">Location: {item.location}</p>
        <p className="text-gray-900 font-semibold mb-2">Contact No: {item.contactNumber}</p>
        <p className="text-gray-900 font-semibold mb-2">Email: {item.email}</p>
        <p className="text-gray-900 font-bold text-lg">Cost: â‚¹{item.price.toFixed(2)}</p>
        <button className="mt-4 px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700" onClick={handleBuyNow}>Buy Now</button>
      </div>
    </div>
  );
};

const App = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = () => {
    setLoading(true);
    axios.get('http://localhost:8005/api/items')
      .then(response => {
        setItems(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError('Error fetching items. Please try again later.');
        setLoading(false);
      });
  };

  return (
    <>
      <Nav />
      <div className="container mx-auto p-4">
        <h1 className="text-2xl font-bold mb-2 text-center">Marketplace</h1>
        {loading && <p className="text-center">Loading...</p>}
        {error && <p className="text-red-500 text-center">{error}</p>}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {items.map(item => (
            <ItemCard key={item.id} item={item} />
          ))}
        </div>
      </div>
    </>
  );
};

export default App;
