import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import ItemService from '../services/ItemService'; 

function CreateItemComponent() {
  const { id } = useParams();
  const navigate = useNavigate(); // Import useNavigate from react-router-dom

  const [material, setMaterial] = useState('');
  const [recyclingInfo, setRecyclingInfo] = useState('');
  const [recycleSuggestion, setRecycleSuggestion] = useState('');
  const [reuseSuggestion, setReuseSuggestion] = useState('');

  useEffect(() => {
    if (id !== '_add') {
      ItemService.getItemById(id).then((res) => {
        let item = res.data;
        setMaterial(item.material);
        setRecyclingInfo(item.recyclingInfo);
        setRecycleSuggestion(item.recycleSuggestion);
        setReuseSuggestion(item.reuseSuggestion);
      });
    }
  }, [id]);

  const saveOrUpdateItem = (e) => {
    e.preventDefault();
    let item = {
      material,
      recyclingInfo,
      recycleSuggestion,
      reuseSuggestion
    };

    if (id === '_add') {
      ItemService.createItem(item).then(() => {
        navigate('/items'); // Use navigate instead of history.push
      });
    } else {
      ItemService.updateItem(item, id).then(() => {
        navigate('/items'); // Use navigate instead of history.push
      });
    }
  };

  const cancel = () => {
    navigate('/items'); // Use navigate instead of history.push
  };

  const getTitle = () => {
    return id === '_add' ? <h3 className="text-center">Add Item</h3> : <h3 className="text-center">Update Item</h3>;
  };

  return (
    <div>
      <div className="container">
        <div className="row">
          <div className="card col-md-6 offset-md-3 offset-md-3">
            {getTitle()}
            <div className="card-body">
              <form>
                <div className="form-group">
                  <label> Material: </label>
                  <input
                    placeholder="Material"
                    name="material"
                    className="form-control"
                    value={material}
                    onChange={(e) => setMaterial(e.target.value)}
                  />
                </div>
                <div className="form-group">
                  <label> Recycling Info: </label>
                  <input
                    placeholder="Recycling Info"
                    name="recyclingInfo"
                    className="form-control"
                    value={recyclingInfo}
                    onChange={(e) => setRecyclingInfo(e.target.value)}
                  />
                </div>
                <div className="form-group">
                  <label> Recycle Suggestion: </label>
                  <input
                    placeholder="Recycle Suggestion"
                    name="recycleSuggestion"
                    className="form-control"
                    value={recycleSuggestion}
                    onChange={(e) => setRecycleSuggestion(e.target.value)}
                  />
                </div>
                <div className="form-group">
                  <label> Reuse Suggestion: </label>
                  <input
                    placeholder="Reuse Suggestion"
                    name="reuseSuggestion"
                    className="form-control"
                    value={reuseSuggestion}
                    onChange={(e) => setReuseSuggestion(e.target.value)}
                  />
                </div>
                <button className="btn btn-success" onClick={saveOrUpdateItem}>
                  Save
                </button>
                <button className="btn btn-danger" onClick={cancel} style={{ marginLeft: "10px" }}>
                  Cancel
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CreateItemComponent;
