import React from 'react';
import { Link } from 'react-router-dom';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/pagination';
import { EffectCoverflow, Pagination } from 'swiper/modules';

import Nav from '../Nav';
import Text from '../text';
import Section from '../Section';
import fact1 from '../images/Fact-1.png';
import fact2 from '../images/Fact-2.png';
import fact3 from '../images/Fact-3.png';
import fact4 from '../images/Fact-4.jpg';
import fact5 from '../images/Fact-5.jpg';
import fact6 from '../images/Fact-6.jpg';
import fact7 from '../images/Fact-7.jpg';
import fact8 from '../images/Fact-8.jpg';
import fact9 from '../images/Fact-9.jpg';

function Home() {
  return (
    <>
      <Nav />
      <div className="flex">
        <div className="w-1/2 p-8">
          <div className="max-w-md">
            <h1 className="mb-5 text-5xl font-bold text-gray-300">Sustainify</h1>
            <p className="mb-5 text-1xl text-gray-200 font-bold">"Sustaninify" is your hub for embracing sustainable living in style. Delve into our sections dedicated to food, gardening, and recycling, where you'll discover a trove of eco-friendly tips, trends, and solutions. From mouthwatering plant-based recipes to lush garden guides and innovative recycling hacks, EcoVibes invites you to elevate your lifestyle while making a positive impact on the planet. Join our vibrant community of eco-enthusiasts and start living in harmony with nature today.</p>
            <Link to="/login"><button className="btn btn-primary">Get Started</button></Link>
          </div>
        </div>
        <div className="w-1/2">
          <Swiper
            effect={'coverflow'}
            grabCursor={true}
            centeredSlides={true}
            slidesPerView={'auto'}
            coverflowEffect={{
              rotate: 50,
              stretch: 0,
              depth: 100,
              modifier: 1,
              slideShadows: true,
            }}
            pagination={true}
            modules={[EffectCoverflow, Pagination]}
            className="mySwiper"
          >
            <SwiperSlide><img src={fact9} alt="fact" /></SwiperSlide>
            <SwiperSlide><img src={fact1} alt="fact" /></SwiperSlide>
            <SwiperSlide><img src={fact8} alt="fact" /></SwiperSlide>
            <SwiperSlide><img src={fact3} alt="fact" /></SwiperSlide>
            <SwiperSlide><img src={fact5} alt="fact" /></SwiperSlide>
            <SwiperSlide><img src={fact4} alt="fact" /></SwiperSlide>
            <SwiperSlide><img src={fact6} alt="fact" /></SwiperSlide>
            <SwiperSlide><img src={fact2} alt="fact" /></SwiperSlide>
            <SwiperSlide><img src={fact7} alt="fact" /></SwiperSlide>
          </Swiper>
        </div>
      </div>
      <Text />
      <Section />
    </>
  );
}

export default Home;
