// src/components/ItemListComponent.jsx
import React, { Component } from 'react';
import ItemService from '../components/services/ItemService';

class ItemListComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      items: []
    };

    this.addItem = this.addItem.bind(this);
    this.editItem = this.editItem.bind(this);
    this.deleteItem = this.deleteItem.bind(this);
  }

  componentDidMount() {
    ItemService.getItems().then((res) => {
      this.setState({ items: res.data });
    });
  }

  addItem() {
    this.props.history.push('/add-item/_add');
  }

  editItem(id) {
    this.props.history.push(`/add-item/${id}`);
  }

  deleteItem(id) {
    ItemService.deleteItem(id).then((res) => {
      this.setState({ items: this.state.items.filter(item => item.id !== id) });
    });
  }

  render() {
    return (
      <div>
        <h2 className="text-center">Items List</h2>
        <div className="row">
          <button className="btn btn-primary" onClick={this.addItem}> Add Item</button>
        </div>
        <br></br>
        <div className="row">
          <table className="table table-striped table-bordered">
            <thead>
              <tr>
                <th> Material</th>
                <th> Recycling Info</th>
                <th> Recycle Suggestion</th>
                <th> Reuse Suggestion</th>
                <th> Actions</th>
              </tr>
            </thead>
            <tbody>
              {this.state.items.map(item =>
                <tr key={item.id}>
                  <td> {item.material} </td>
                  <td> {item.recyclingInfo}</td>
                  <td> {item.recycleSuggestion}</td>
                  <td> {item.reuseSuggestion}</td>
                  <td>
                    <button onClick={() => this.editItem(item.id)} className="btn btn-info">Update</button>
                    <button onClick={() => this.deleteItem(item.id)} className="btn btn-danger" style={{ marginLeft: "10px" }}>Delete</button>
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default ItemListComponent;
