import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar, Pie } from 'react-chartjs-2';
import Nav from "./Nav";

const Dashboard = () => {
  const [totalWaste, setTotalWaste] = useState(0);
  const [categoryData, setCategoryData] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const response = await axios.get('http://localhost:8005/api/dashboard');
      const { totalWasteOrdered, wasteCategoryData } = response.data;
      
      setTotalWaste(totalWasteOrdered);

      const categories = Object.keys(wasteCategoryData);
      const quantities = Object.values(wasteCategoryData);

      setCategoryData({
        labels: categories,
        datasets: [{
          label: 'Waste Quantity (kg)',
          data: quantities,
          backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
        }]
      });

      setLoading(false);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setLoading(false);
    }
  };

  if (loading) {
    return <p className="text-center">Loading...</p>;
  }

  return (
    <>
      <Nav />
      <div className="bg-white">
        <div className="container mx-auto p-4">
          <h1 className="text-2xl font-bold mb-4 text-center">Waste Management Dashboard</h1>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            <div className="bg-white shadow-md rounded-lg p-4">
              <h2 className="text-xl font-semibold mb-2">Total Waste Ordered</h2>
              <p className="text-3xl font-bold text-indigo-600">{totalWaste} kg</p>
            </div>
            <div className="bg-white shadow-md rounded-lg p-4">
              <h2 className="text-xl font-semibold mb-2">Category-wise Waste Sold</h2>
              <Bar data={categoryData} options={{ maintainAspectRatio: false }} />
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Dashboard;
