import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Nav from "./Nav";
import { useHistory } from 'react-router-dom';

const ItemCard = ({ item }) => {
  const handleBuyNow = () => {
    const phoneNumber = '8277492748';
    const whatsappUrl = `https://wa.me/${phoneNumber}`;
    window.open(whatsappUrl, '_blank');
  };

  return (
    <div className="bg-white shadow-lg rounded-lg overflow-hidden transform transition duration-500 hover:scale-105">
      {item.photoUrl && (
        <img
          src={`http://localhost:8005/api/items/photos/${item.photoUrl}`}
          alt={item.title}
          className="w-full h-64 object-cover"
        />
      )}
      <div className="p-4">
        <h2 className="text-xl font-bold mb-2">{item.title}</h2>
        <p className="text-gray-700 mb-2">{item.description}</p>
        <p className="text-gray-900 font-semibold mb-2">Category: {item.category}</p>
        <p className="text-gray-900 font-semibold mb-2">Quantity: {item.quantity}</p>
        <p className="text-gray-900 font-semibold mb-2">Location: {item.location}</p>
        <p className="text-gray-900 font-semibold mb-2">Contact No: {item.contactNumber}</p>
        <p className="text-gray-900 font-semibold mb-2">Email: {item.email}</p>
        <p className="text-gray-900 font-bold text-lg">Cost: ₹{item.price.toFixed(2)}</p>
        <button className="mt-4 px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700" onClick={handleBuyNow}>Contact Seller</button>
      </div>
    </div>
  );
};

const App = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [category, setCategory] = useState('');
  const [location, setLocation] = useState('');

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = () => {
    setLoading(true);
    axios.get('http://localhost:8005/api/items')
      .then(response => {
        setItems(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError('Error fetching items. Please try again later.');
        setLoading(false);
      });
  };

  const filteredItems = items.filter(item => {
    return (
      (category ? item.category === category : true) &&
      (location ? item.location === location : true)
    );
  });

  return (
    <>
      <Nav />
      <div className="bg-white">
        <div className="container mx-auto p-4">
          <h1 className="text-2xl font-bold mb-1 text-center">Marketplace</h1>
          <br />
          <div className="flex justify-center mb-4">
            <select
              className="mx-2 p-2 border rounded"
              value={category}
              onChange={e => setCategory(e.target.value)}
            >
              <option value="">All Categories</option>
              <option value="Electronics">Dry Waste</option>
              <option value="Clothing">E-Waste</option>
              <option value="Books">Organic Waste</option>
              {/* Add more categories as needed */}
            </select>
            <select
              className="mx-2 p-2 border rounded"
              value={location}
              onChange={e => setLocation(e.target.value)}
            >
              <option value="">All Locations</option>
              <option value="New York">Yellur</option>
              <option value="Los Angeles">Vadgaon</option>
              <option value="Chicago">Hindwadi</option>
              {/* Add more locations as needed */}
            </select>
          </div>
          {loading && <p className="text-center">Loading...</p>}
          {error && <p className="text-red-500 text-center">{error}</p>}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredItems.map(item => (
              <ItemCard key={item.id} item={item} />
            ))}
          </div>
        </div>
      </div>
    </>
  );
};

export default App;
