import React, { useEffect, useState } from 'react';
import axios from 'axios';

const App = () => {
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState({
    title: '',
    description: '',
    category: '',
    price: '',
    condition: '',
    location: '',
    photoUrl: '',
    contactNumber: '',
  });

  // Fetch all items when the component mounts
  useEffect(() => {
    fetchItems();
  }, []);

  // Fetch all items from the backend
  const fetchItems = () => {
    axios.get('http://localhost:8005/api/items')
      .then(response => {
        setItems(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the items!', error);
      });
  };

  // Handle adding a new item
  const handleAddItem = () => {
    axios.post('http://localhost:8005/api/items', newItem)
      .then(() => {
        // After adding the item, fetch all items again to update the list
        fetchItems();
        // Clear the newItem state to reset the form
        setNewItem({
          title: '',
          description: '',
          category: '',
          price: '',
          condition: '',
          location: '',
          photoUrl: '',
          contactNumber: '',
        });
      })
      .catch(error => {
        console.error('There was an error adding the item!', error);
      });
  };

  // Handle deleting an item by its ID
  const handleDeleteItem = (itemId) => {
    axios.delete(`http://localhost:8005/api/items/${itemId}`)
      .then(() => {
        // After deleting the item, fetch all items again to update the list
        fetchItems();
      })
      .catch(error => {
        console.error('There was an error deleting the item!', error);
      });
  };

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewItem(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <div className="container mx-auto p-4">
      {/* Form to add a new item */}
      <div className="mb-4">
        <h2 className="text-lg font-semibold mb-2">Add New Item</h2>
        {/* Input fields for each item property */}
        <input type="text" name="title" value={newItem.title} onChange={handleChange} placeholder="Title" className="border border-gray-300 px-4 py-2 mb-2" />
        <input type="text" name="description" value={newItem.description} onChange={handleChange} placeholder="Description" className="border border-gray-300 px-4 py-2 mb-2" />
        <input type="text" name="category" value={newItem.category} onChange={handleChange} placeholder="Category" className="border border-gray-300 px-4 py-2 mb-2" />
        <input type="number" name="price" value={newItem.price} onChange={handleChange} placeholder="Price" className="border border-gray-300 px-4 py-2 mb-2" />
        <input type="text" name="condition" value={newItem.condition} onChange={handleChange} placeholder="Condition" className="border border-gray-300 px-4 py-2 mb-2" />
        <input type="text" name="location" value={newItem.location} onChange={handleChange} placeholder="Location" className="border border-gray-300 px-4 py-2 mb-2" />
        <input type="text" name="photoUrl" value={newItem.photoUrl} onChange={handleChange} placeholder="Photo URL" className="border border-gray-300 px-4 py-2 mb-2" />
        <input type="text" name="contactNumber" value={newItem.contactNumber} onChange={handleChange} placeholder="Contact Number" className="border border-gray-300 px-4 py-2 mb-2" />
        {/* Button to add the new item */}
        <button onClick={handleAddItem} className="bg-blue-500 text-white px-4 py-2 rounded-lg">Add Item</button>
      </div>

      {/* Display existing items */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {/* Map over the items array and render each item card */}
        {items.map(item => (
          <div key={item.id} className="bg-white shadow-md rounded-lg overflow-hidden">
            {/* Display item details */}
            <div className="p-4">
              <h2 className="text-xl font-bold mb-2">{item.title}</h2>
              <p className="text-gray-700 mb-2">{item.description}</p>
              <p className="text-gray-900 font-semibold mb-2">Category: {item.category}</p>
              <p className="text-gray-900 font-semibold mb-2">Condition: {item.condition}</p>
              <p className="text-gray-900 font-semibold mb-2">Location: {item.location}</p>
              <p className="text-gray-900 font-semibold mb-2">Contact: {item.contactNumber}</p>
              <p className="text-gray-900 font-bold text-lg">â‚¹{item.price.toFixed(2)}</p>
            </div>
            {/* Button to delete the item */}
            <button onClick={() => handleDeleteItem(item.id)} className="bg-red-500 text-white px-4 py-2 rounded-lg mb-4">Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
