import React, { useState, useEffect } from 'react';
import PostService from './PostService';

const PostList = () => {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        loadPosts();
    }, []);

    const loadPosts = async () => {
        const result = await PostService.getPosts();
        setPosts(result.data);
    };

    const deletePost = async (id) => {
        await PostService.deletePost(id);
        loadPosts();
    };

    return (
        <div className="container mx-auto px-4 pt-8 pb-16">
            <h2 className="text-center text-3xl font-bold text-gray-800 mb-8">Recycling Ideas</h2>
            {posts.map(post => (
                <div key={post.id} className="bg-white shadow-md rounded-lg overflow-hidden mb-8">
                    <div className="p-6">
                        <div className="flex items-center mb-4">
                            <div className="h-12 w-12 rounded-full bg-gray-300"></div>
                            <div className="ml-4">
                                <div className="text-lg font-semibold text-gray-900">{post.title}</div>
                                <div className="text-sm text-gray-500">{post.name}</div>
                            </div>
                            <div className="ml-auto text-sm text-gray-500">
                                {new Date(post.date).toLocaleDateString()}
                            </div>
                        </div>
                        <p className="text-gray-700">{post.content}</p>
                    </div>
                    <div className="px-6 py-4 bg-gray-100 flex justify-between">
                        <button
                            onClick={() => deletePost(post.id)}
                            className="bg-red-500 text-white px-6 py-2 rounded-full hover:bg-red-600"
                        >
                            Delete
                        </button>
                        <div className="text-sm text-gray-500">{post.email}</div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default PostList;
