// src/components/CreateItemComponent.jsx
import React, { Component } from 'react';
import ItemService from '../components/services/ItemService';
class CreateItemComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: this.props.match.params.id,
      material: '',
      recyclingInfo: '',
      recycleSuggestion: '',
      reuseSuggestion: ''
    };

    this.changeMaterialHandler = this.changeMaterialHandler.bind(this);
    this.changeRecyclingInfoHandler = this.changeRecyclingInfoHandler.bind(this);
    this.changeRecycleSuggestionHandler = this.changeRecycleSuggestionHandler.bind(this);
    this.changeReuseSuggestionHandler = this.changeReuseSuggestionHandler.bind(this);
    this.saveOrUpdateItem = this.saveOrUpdateItem.bind(this);
  }

  componentDidMount() {
    if (this.state.id === '_add') {
      return;
    } else {
      ItemService.getItemById(this.state.id).then((res) => {
        let item = res.data;
        this.setState({
          material: item.material,
          recyclingInfo: item.recyclingInfo,
          recycleSuggestion: item.recycleSuggestion,
          reuseSuggestion: item.reuseSuggestion
        });
      });
    }
  }

  saveOrUpdateItem = (e) => {
    e.preventDefault();
    let item = {
      material: this.state.material,
      recyclingInfo: this.state.recyclingInfo,
      recycleSuggestion: this.state.recycleSuggestion,
      reuseSuggestion: this.state.reuseSuggestion
    };

    if (this.state.id === '_add') {
      ItemService.createItem(item).then(res => {
        this.props.history.push('/items');
      });
    } else {
      ItemService.updateItem(item, this.state.id).then(res => {
        this.props.history.push('/items');
      });
    }
  }

  changeMaterialHandler = (event) => {
    this.setState({ material: event.target.value });
  }

  changeRecyclingInfoHandler = (event) => {
    this.setState({ recyclingInfo: event.target.value });
  }

  changeRecycleSuggestionHandler = (event) => {
    this.setState({ recycleSuggestion: event.target.value });
  }

  changeReuseSuggestionHandler = (event) => {
    this.setState({ reuseSuggestion: event.target.value });
  }

  cancel() {
    this.props.history.push('/items');
  }

  getTitle() {
    if (this.state.id === '_add') {
      return <h3 className="text-center">Add Item</h3>
    } else {
      return <h3 className="text-center">Update Item</h3>
    }
  }

  render() {
    return (
      <div>
        <div className="container">
          <div className="row">
            <div className="card col-md-6 offset-md-3 offset-md-3">
              {this.getTitle()}
              <div className="card-body">
                <form>
                  <div className="form-group">
                    <label> Material: </label>
                    <input placeholder="Material" name="material" className="form-control"
                      value={this.state.material} onChange={this.changeMaterialHandler} />
                  </div>
                  <div className="form-group">
                    <label> Recycling Info: </label>
                    <input placeholder="Recycling Info" name="recyclingInfo" className="form-control"
                      value={this.state.recyclingInfo} onChange={this.changeRecyclingInfoHandler} />
                  </div>
                  <div className="form-group">
                    <label> Recycle Suggestion: </label>
                    <input placeholder="Recycle Suggestion" name="recycleSuggestion" className="form-control"
                      value={this.state.recycleSuggestion} onChange={this.changeRecycleSuggestionHandler} />
                  </div>
                  <div className="form-group">
                    <label> Reuse Suggestion: </label>
                    <input placeholder="Reuse Suggestion" name="reuseSuggestion" className="form-control"
                      value={this.state.reuseSuggestion} onChange={this.changeReuseSuggestionHandler} />
                  </div>
                  <button className="btn btn-success" onClick={this.saveOrUpdateItem}>Save</button>
                  <button className="btn btn-danger" onClick={this.cancel.bind(this)} style={{ marginLeft: "10px" }}>Cancel</button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default CreateItemComponent;
