import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Nav from "./Nav";
import { useHistory } from 'react-router-dom';

const ItemCard = ({ item }) => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    phoneNumber: '',
    address: '',
    pincode: '',
    quantity: 1,
    totalPrice: item.price.toFixed(2)
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    if (name === 'quantity') {
      const total = (parseFloat(value) * item.price).toFixed(2);
      setFormData({ ...formData, totalPrice: total });
    }
  };

  const handleBuyNow = () => {
    // Implement validation here if needed
    // Submit formData to backend, for example:
    axios.post('http://localhost:8080/api/orders', {
      ...formData,
      itemId: item.id,
      itemName: item.title,
      itemPrice: item.price
    })
    .then(response => {
      alert('Order placed successfully!');
      // Optionally redirect or perform other actions
    })
    .catch(error => {
      alert('Error placing order. Please try again.');
      console.error(error);
    });
  };

  return (
    <div className="bg-white shadow-lg rounded-lg overflow-hidden transform transition duration-500 hover:scale-105">
      {item.photoUrl && (
        <img
          src={`http://localhost:8005/api/items/photos/${item.photoUrl}`}
          alt={item.title}
          className="w-full h-64 object-cover"
        />
      )}
      <div className="p-4">
        <h2 className="text-xl font-bold mb-2">{item.title}</h2>
        <p className="text-gray-700 mb-2">{item.description}</p>
        <p className="text-gray-900 font-semibold mb-2">Category: {item.category}</p>
        <p className="text-gray-900 font-semibold mb-2">Quantity: {item.quantity}</p>
        <p className="text-gray-900 font-semibold mb-2">Location: {item.location}</p>
        <p className="text-gray-900 font-semibold mb-2">Contact No: {item.contactNumber}</p>
        <p className="text-gray-900 font-semibold mb-2">Email: {item.email}</p>
        <p className="text-gray-900 font-bold text-lg">Cost: ₹{item.price.toFixed(2)}</p>
        
        {/* Form for buying */}
        <form className="mt-4">
          <label className="block mb-2">
            First Name:
            <input type="text" name="firstName" value={formData.firstName} onChange={handleInputChange} required className="form-input mt-1 block w-full" />
          </label>
          <label className="block mb-2">
            Last Name:
            <input type="text" name="lastName" value={formData.lastName} onChange={handleInputChange} required className="form-input mt-1 block w-full" />
          </label>
          <label className="block mb-2">
            Phone Number:
            <input type="tel" name="phoneNumber" value={formData.phoneNumber} onChange={handleInputChange} required className="form-input mt-1 block w-full" />
          </label>
          <label className="block mb-2">
            Address:
            <textarea name="address" value={formData.address} onChange={handleInputChange} required className="form-textarea mt-1 block w-full" />
          </label>
          <label className="block mb-2">
            Pincode:
            <input type="text" name="pincode" value={formData.pincode} onChange={handleInputChange} required className="form-input mt-1 block w-full" />
          </label>
          <label className="block mb-2">
            Quantity:
            <input type="number" name="quantity" value={formData.quantity} onChange={handleInputChange} min="1" required className="form-input mt-1 block w-full" />
          </label>
          <p className="text-gray-900 font-bold">Total Cost: ₹{formData.totalPrice}</p>
          <button type="button" className="mt-4 px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700" onClick={handleBuyNow}>Buy Now</button>
        </form>
        
        {/* Contact Seller button */}
        <button className="mt-4 px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700" onClick={() => window.open(`https://wa.me/${item.contactNumber}`, '_blank')}>Contact Seller</button>
      </div>
    </div>
  );
};

const App = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = () => {
    setLoading(true);
    axios.get('http://localhost:8005/api/items')
      .then(response => {
        setItems(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError('Error fetching items. Please try again later.');
        setLoading(false);
      });
  };

  return (
    <>
      <Nav />
      <div className="bg-white">
        <div className="container mx-auto p-4">
          <h1 className="text-2xl font-bold mb-1 text-center">Marketplace</h1>
          <br></br>
          {loading && <p className="text-center">Loading...</p>}
          {error && <p className="text-red-500 text-center">{error}</p>}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {items.map(item => (
              <ItemCard key={item.id} item={item} />
            ))}
          </div>
        </div>
      </div>
    </>
  );
};

export default App;
