import React, { useEffect, useState } from "react";
import materialData from "./data.json";
import './styless.css'
import Nav from '../Nav'

function App() {
  const [searchResults, setSearchResults] = useState([]);
  const [query, setQuery] = useState("");

  useEffect(() => {
    setSearchResults(materialData);
  }, []);

  function search(data) {
    return data.filter((item) =>
      item.material.toLowerCase().includes(query.toLowerCase())
    );
  }

  return (
    <>
      <Nav />
      <div className="containers">
        <center>
          <h1>Search for the item</h1>
        </center>
        <div className="input-box">
          <input
            type="search"
            name="search-form"
            id="search-form"
            className="search-input"
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Search material" />
        </div>
        <center>
          {search(searchResults).map((item) => {
            return (
              <div className="box" key={item.id}>
                <div className="cards">
                  <div className="category">{item.material}</div>
                  <div className="heading">
                    {item.recyclingInfo}
                  </div>
                </div>
              </div>
            );
          })}
        </center>
      </div>
    </>
  );
}

export default App;
