import React, { useState } from 'react';
import axios from 'axios';

const AddItem = () => {
    const [item, setItem] = useState({
        title: '',
        description: '',
        category: '',
        price: '',
        condition: '',
        location: '',
        photoUrl: '', // Add this field
        contactNumber: '' // Add this field
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setItem(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:8005/api/items', item, {
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(response => {
            console.log('Item added:', response.data);
            setItem({
                title: '',
                description: '',
                category: '',
                price: '',
                condition: '',
                location: '',
                photoUrl: '', // Reset this field
                contactNumber: '' // Reset this field
            });
        })
        .catch(error => {
            console.error('Error adding item:', error);
        });
    };

    return (
        <div>
            <h2>Add Item</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Title:</label>
                    <input
                        type="text"
                        name="title"
                        value={item.title}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Description:</label>
                    <input
                        type="text"
                        name="description"
                        value={item.description}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Category:</label>
                    <input
                        type="text"
                        name="category"
                        value={item.category}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Price:</label>
                    <input
                        type="number"
                        name="price"
                        value={item.price}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Condition:</label>
                    <input
                        type="text"
                        name="condition"
                        value={item.condition}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Location:</label>
                    <input
                        type="text"
                        name="location"
                        value={item.location}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Photo URL:</label>
                    <input
                        type="text"
                        name="photoUrl"
                        value={item.photoUrl}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Contact Number:</label>
                    <input
                        type="text"
                        name="contactNumber"
                        value={item.contactNumber}
                        onChange={handleChange}
                    />
                </div>
                <button type="submit">Add Item</button>
            </form>
        </div>
    );
};

export default AddItem;
