import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import PostService from './PostService';

const CreatePost = () => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const navigate = useNavigate();

    const savePost = async (e) => {
        e.preventDefault();
        let post = { title, content, name, email };
        await PostService.createPost(post);
        navigate('/posts');
    };

    return (
        <div className="max-w-2xl mx-auto mt-8 bg-white shadow-md rounded-lg p-4">
            <h2 className="text-center text-2xl font-bold mb-4">Add Post</h2>
            <form onSubmit={savePost}>
                <div className="mb-4">
                    <label className="block text-gray-900 text-sm font-bold mb-2">Name</label>
                    <input
                        type="text"
                        placeholder="Name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        className="w-full px-3 py-2 border rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-gray-900 text-sm font-bold mb-2">Email</label>
                    <input
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        className="w-full px-3 py-2 border rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-gray-900 text-sm font-bold mb-2">Title</label>
                    <input
                        type="text"
                        placeholder="Title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        className="w-full px-3 py-2 border rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-gray-900 text-sm font-bold mb-2">Content</label>
                    <textarea
                        placeholder="What's happening?"
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                        className="w-full h-32 px-3 py-2 border rounded-md text-white focus:outline-none focus:ring-5 focus:ring-blue-500"
                    ></textarea>
                </div>
                <div className="flex justify-end">
                    <button
                        type="submit"
                        className="bg-blue-500 text-white font-bold py-2 px-4 rounded-full hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                        Post
                    </button>
                </div>
            </form>
        </div>
    );
};

export default CreatePost;
