export function getRangeValidationResult(
  value: RangeValue<DateValue>,
  minValue: DateValue,
  maxValue: DateValue,
  isDateUnavailable: (v: DateValue) => boolean,
  options: FormatterOptions
) {
  let startValidation = getValidationResult(
    value?.start,
    minValue,
    maxValue,
    isDateUnavailable,
    options
